  const [showModal, setShowModal] = useState (false)

  const [showEvents, setShowEvents] = useState (true)

  const[events, setEvent] = useState([
    {title:'Evento 1', id:1},
    {title:'Evento 2', id:2},
    {title:'Evento 3', id:3}
  ])

  return (
    <div className="App">
      <Title title = "Title 1" subtitle="Subtitle 1"/>

