import './App.css';
import {useState} from 'react'

function App() {

  const[events, setEvent] = useState([
    {title:'Evento 1', id:1},
    {title:'Evento 2', id:2},
    {title:'Evento 3', id:3}
  ])

  const handleClick = (id) => {
    setEvent(events.filter((event) => {
      return event.id !== id
    }))

  } 

  return (
    <div className="App">
      {events.map((event, index) => (
        <div key={event.id}>
          <h2>{index} - {event.title}</h2>
          <button onClick={() => handleClick(event.id)}>remove event</button>
        </div> 
      ))}
    </div>
  );
}

export default App;
