import './App.css';
import {useState} from 'react'

function App() {

  const [showEvents, setShowEvents] = useState (true)

  const[events, setEvent] = useState([
    {title:'Evento 1', id:1},
    {title:'Evento 2', id:2},
    {title:'Evento 3', id:3}
  ])

  const handleClick = (id) => {
    setEvent((prevEvents) => {
      return prevEvents.filter((event) => {
        return event.id !== id
      })
    })
  } 

  return (
    <div className="App">
      {showEvents && (<button onClick={() => {setShowEvents(true)}}>hide events</button>)}
      {!showEvents && (<button onClick={() => {setShowEvents(false)}}>show events</button>)}

      {showEvents && events.map((event, index) => (
        <div key={event.id}>
          <h2>{index} - {event.title}</h2>
          <button onClick={() => handleClick(event.id)}>remove event</button>
        </div> 
      ))}
    </div>
  );
}

export default App;
